import React, { useRef } from "react";
import { Link } from "react-router-dom";
import axios from 'axios';
function Register() {
    const details = {
        name: '',
        email: '',
        password: '',
        confirmPassword: ''
    }

    const inputNameRef = useRef();
    const inputEmailRef = useRef();
    const inputPasswordRef = useRef();
    const inputConfirmPasswordRef = useRef();
    const validateCredentials = () => {
        details.name = inputNameRef.current.value
        details.email = inputEmailRef.current.value
        details.password = inputPasswordRef.current.value
        details.confirmPassword = inputConfirmPasswordRef.current.value
        const config = {
            headers: {
              "Access-Control-Allow-Origin": "*",
              "Access-Control-Allow-Methods": "GET,PUT,POST,DELETE,PATCH,OPTIONS"
            }
        };
        axios.post('http://localhost:5000/register',config).then(function (response) {
            console.log(response);
            // if(response.data.password!=null){
            //           let path='/Landing';
            //           const data=response.data;
            //           history.push({
            //             pathname: path,
            //             state: { detail: response.data }
            //           });
            // }
            // else{
            //   pass.value=""
            //   email.value="";
            //     if(response.data.email==null){
            //       setErrorMessage("Your account has been locked");
            //     }
            //     else{
            //           setErrorMessage('Password is incorrect');
            //     }

            // }
          }).catch(function (error) {
                // setErrorMessage('Account does not exist');
                console.log(error)
          });
    }
    return(
        <div className='Register'>
            <form>
                <div class="form-group">
                <label for="name">Name</label>
                <input
                    type="name"
                    id="name"
                    name="name"
                    class="form-control"
                    placeholder="Enter Name"
                    ref={inputNameRef}
                />
                </div>
                <div class="form-group">
                <label for="email">Email</label>
                <input
                    type="email"
                    id="email"
                    name="email"
                    class="form-control"
                    placeholder="Enter Email"
                    ref={inputEmailRef}
                />
                </div>
                <div class="form-group">
                <label for="password">Password</label>
                <input
                    type="password"
                    id="password"
                    name="password"
                    class="form-control"
                    placeholder="Create Password"
                    ref={inputPasswordRef}
                />
                </div>
                <div class="form-group">
                <label for="password2">Confirm Password</label>
                <input
                    type="password"
                    id="password2"
                    name="password2"
                    class="form-control"
                    placeholder="Confirm Password"
                    ref={inputConfirmPasswordRef}
                />
                </div>
                <input type="button" class="btn btn-primary btn-block" value="Register" onClick={validateCredentials}/>
            </form>
            <p class="lead mt-4">Have An Account? <Link to="/login"><h2> Login</h2></Link></p>
        </div>
    )

}

export default React.memo(Register);
